(function() {
	tinymce.create('tinymce.plugins.opal_page_image', {
		init : function(ed, url) {
			// Register commands
			
			ed.addCommand('mce_opal_page_image', function() {
				// Internal image object like a flash placeholder
				if (ed.dom.getAttrib(ed.selection.getNode(), 'class').indexOf('mceItem') != -1)
					return;

				ed.windowManager.open({
					file : url + '/pages/tinymce_images',
					width : 480 + parseInt(ed.getLang('advimage.delta_width', 0)),
					height : 385 + parseInt(ed.getLang('advimage.delta_height', 0)),
					inline : 1
				}, {
					plugin_url : url
				});
			});

			// Register buttons
			ed.addButton('opal_page_image', {
				title : 'advimage.image_desc',
				cmd : 'mce_opal_page_image',
         		image : url + '/imgs/images.png'			
			});
		},

		getInfo : function() {
			return {
				longname : 'Advanced image',
				author : 'Moxiecode Systems AB',
				authorurl : 'http://tinymce.moxiecode.com',
				infourl : 'http://wiki.moxiecode.com/index.php/TinyMCE:Plugins/advimage',
				version : tinymce.majorVersion + "." + tinymce.minorVersion
			};
		}
	});

	// Register plugin
	tinymce.PluginManager.add('opal_page_image', tinymce.plugins.opal_page_image);
})();